$(function(){
  $("*[data-include-html]").each(function(){
    var t = $(this).attr("data-include-html"),
        c = $(this).attr("data-include-html-callback"),
        u = this.id;
        jQuery.ajax({
          url: t,
          success: function(t){
            $("#" + u).html(t)
            if (typeof window[c] == 'function')
              window[c]();
          },
          error: function(n, c, i){
            var s = n.status + ": " + n.statusText;
            $("#" + u).html(t + "-" + s)
          }
        })
  })
});